    public static ArrayList<String> compAllSeq(String[] s, int k){
        if (k==1)
            return new ArrayList<String>(Arrays.asList(s));
        else {
            ArrayList<String> l = compAllSeq(s, k-1);
            ArrayList<String> l1 = new ArrayList<>();

            for (String str: l) {
                for (String ch:s) {
                    String str1 = ch+str;
                    l1.add(str1);
                }
            }
            return l1;
        }

    }
